		<div class="item">
			<h3>Sitemap</h3>
			<ul>
				<li class:active={$page.url.pathname === '/'} class="link">
					<a href="/">Home</a>
				</li>
				<li class:active={$page.url.pathname === '/testimonials'} class="link">
					<a href="/testimonials">Testimonials</a>
				</li>
				<li class:active={$page.url.pathname === '/portfolio'} class="link">
					<a href="/portfolio">Portfolio</a>
				</li>
				<li class:active={$page.url.pathname === '/about'} class="link">
					<a href="/about">About</a>
				</li>
				<li class:active={$page.url.pathname === '/contact'} class="link">
					<a href="/contact">Contact</a>
				</li>
			</ul>
		</div>
		<div class="item">
			<h3>Services</h3>
			<ul>
				{#each businessInfo.ftServices as { service }}
					<li>
						<a href="/">{service}</a>
					</li>
				{/each}
			</ul>
		</div>
		<div class="item">
			<div class="subitem">
				<h3>Keep in touch</h3>
				<div class="socialmedia">
					{#each businessInfo.socials as social}
						{#if social.url.length >= 1}
							<a class={social.platform} aria-label={social.platform} href={social.url}>
								<img
									class=""
									src="/social/{social.platform}.svg"
									alt=""
									width="40"
									height="34"
									loading="lazy"
									decoding="async"
								/>
							</a>
						{/if}
					{/each}
				</div>
			</div>
			<div class="subitem">
				<h3>Contact Us</h3>
				<ul>
					<li>
						<a href="mailto:{businessInfo.email}">Click to Email</a>
					</li>
					<li>
						<a href="tel:{businessInfo.phone}"><span>Phone:</span> {businessInfo.phone}</a>
					</li>
					<li>
						<a href={businessInfo.googleLink}><span>Location:</span> {businessInfo.fullAddress}</a>
					</li>
				</ul>
			</div>
		</div>
	</div>
</footer>
